class KthLargest {
    int kth_element; // to store the index of element you want to return
    ArrayList<Integer> elements; // Maintaing arraylist of element
    public KthLargest(int k, int[] nums) {
    kth_element=k;
    elements = new ArrayList<Integer>();
    Collections.addAll(elements, Arrays.stream(nums).boxed().toArray(Integer[]::new)); // Coz Beauty of Collections 
