    public int add(int val) {
    elements.add(val); // value is added to the last index of element array
	if(val <= elements.get(elements.size()-1)) //Sort only if the value added is less than the last element
    Collections.sort(elements);
    return elements.get(elements.size()-1-kth_element+1); // return the kth_largest by subtracting indec
    }
}

