#define NMAX 100000

class MKAverage {
private:
    vector<long long> itCnt, pos;
    vector<long long> itSum;
    deque<int> nums;
    int m, k;
public:
    void initIT(int i, int l, int r){
        if (l == r){
            pos[l] = i;
            return;
        }    
        int mid = (l + r) / 2;
        initIT(i * 2, l, mid);
        initIT(i * 2 + 1, mid + 1, r);
    }

    void update(int i, int valSum, int valCnt){
        while (i != 0){
            itSum[i] += valSum;
            itCnt[i] += valCnt;
            i = i / 2;
        }    
    }

    MKAverage(int m, int k) {
        itCnt.resize(4 * NMAX + 9);
        pos.resize(NMAX + 9);
        itSum.resize(4 * NMAX + 9);
        initIT(1, 1, NMAX);
        this->m = m;
        this->k = k;
    }

    int findPosOfKthLargest(int i, int l, int r, int K){
        if (l == r){
            return l;
        }
        int mid = (l + r) / 2;
        if (itCnt[i * 2] >= K){
            return findPosOfKthLargest(i * 2, l, mid, K);
        } else {
            K = K - itCnt[i * 2];
            return findPosOfKthLargest(i * 2 + 1, mid + 1, r, K);
        }
    }

    void getSum(int i, int l, int r, int u, int v, long long &sum, vector<long long> &itSum){
        if (u > v) return;
        if (l > v || r < u) return;
        int mid = (l + r) / 2;
        if (u <= l && r <= v){
            sum = sum + itSum[i];
            return;
        }
        getSum(i * 2, l, mid, u, v, sum, itSum);
        getSum(i * 2 + 1, mid + 1, r, u, v, sum, itSum);
    }

    void addElement(int num) {
        int valPop = -1;
        if (nums.size() < m){
            nums.push_back(num);
        } else {
            nums.push_back(num);
            valPop = nums.front();
            nums.pop_front();
        }
        if (valPop != -1){
            update(pos[valPop], -valPop, -1);
        }
        update(pos[num], num, 1);
    }

    int calculateMKAverage() {
        if (nums.size() < m) return -1;
        int l = findPosOfKthLargest(1, 1, NMAX, k + 1);
        int r = findPosOfKthLargest(1, 1, NMAX, m - k);
        long long sum = 0;
        long long cntElementLeft = 0;
        long long cntElementRight = 0;
        getSum(1, 1, NMAX, l, r, sum, this->itSum);
        getSum(1, 1, NMAX, 1, l - 1, cntElementLeft, this->itCnt);
        getSum(1, 1, NMAX, r + 1, NMAX, cntElementRight, this->itCnt);
        sum = sum - (k - cntElementLeft) * l;
        sum = sum - (k - cntElementRight) * r;
        int res = sum / (m - 2 * k);
        return res;
    }
};

