func (this *MKAverage) AddElement(num int)  {
    if this.pos >= this.m {
        this.remove(this.circArr[this.pos % this.m])
    }
    this.add(num)
    this.circArr[this.pos % this.m] = num
    this.pos += 1  
}


func (this *MKAverage) CalculateMKAverage() int {
    if this.pos < this.m {
        return -1
    }
    return this.sum / this.sz
}

func (this *MKAverage) add(num int) {
    this.left.insert(num)
    if this.left.length() > this.k {
        val := this.left.removeLast()
        this.mid.insert(val)
        this.sum += val
    }

    if this.mid.length() > this.sz {
        val := this.mid.removeLast()
        this.right.insert(val)
        this.sum -= val
    }
}

func (this *MKAverage) remove(n int) {
    if !this.left.remove(n) && !this.right.remove(n) {
        this.mid.remove(n)
        this.sum -= n
    }

    if this.left.length() < this.k {
        val := this.mid.removeFirst()
        this.left.insert(val)
        this.sum -= val
    }

    if this.mid.length() < this.sz {
        val := this.right.removeFirst() 
        this.mid.insert(val)
        this.sum += val
    }
}


type sortedArr struct {
    buf []int
}

func (bs *sortedArr) length() int {
    return len(bs.buf)
}

func (bs *sortedArr) insert(val int) {
    idx := bs.searchAsc(val)  

    bs.buf = append(bs.buf, 0)
    copy(bs.buf[idx+1:], bs.buf[idx:])
    bs.buf[idx]=val
}

func (bs *sortedArr) remove(val int) bool {
    idx := bs.searchAsc(val) 
    if idx < len(bs.buf) && bs.buf[idx] == val{
        bs.buf = append(bs.buf[:idx], bs.buf[idx+1:]...)
        return true
    }
    return false
}

func (bs *sortedArr) removeLast() int {
    val := bs.buf[len(bs.buf)-1]
    bs.buf = bs.buf[:len(bs.buf)-1]
    return val
}

func (bs *sortedArr) removeFirst() int {
    val := bs.buf[0]
    bs.buf = bs.buf[1:]
    return val
}

func (bs *sortedArr) searchAsc(val int) int{
    idx := sort.Search(len(bs.buf), func(i int) bool {
        return bs.buf[i] >= val
    }) 

    return idx
}

