class MKAverage:
    """
    Use bisect module to keep a sorted list
    """

    def __init__(self, m: int, k: int):
        self.latest_m = collections.deque()
        self.sorted_list = []
        self.m = m
        self.k = k
        # sum of smallest k elements
        self.sum_k = 0
        # sum of smallest m - k elements
        self.sum_m_k = 0

    def addElement(self, num: int) -> None:
        self.latest_m.append(num)
        insert_index = bisect.bisect_left(self.sorted_list, num)

        if len(self.latest_m) > self.m:
            old = self.latest_m.popleft()
            # since it is guaranteed that the old number
            # will be in the sorted_list, use bisect_left to
            # get it's index
            remove_index = bisect.bisect_left(self.sorted_list, old)
            if remove_index <= self.k:
                self.sum_k -= self.sorted_list[remove_index]
                self.sum_k += self.sorted_list[self.k]
            if remove_index <= self.m - self.k:
                self.sum_m_k -= self.sorted_list[remove_index]
                self.sum_m_k += self.sorted_list[self.m - self.k]

            self.sorted_list.pop(remove_index)

        bisect.insort_left(self.sorted_list, num)
        if insert_index <= self.k:
            self.sum_k += num
            if self.k < len(self.sorted_list):
                self.sum_k -= self.sorted_list[self.k]
        if insert_index <= self.m - self.k:
            self.sum_m_k += num
            if self.m-self.k < len(self.sorted_list):
                self.sum_m_k -= self.sorted_list[self.m - self.k]


    def calculateMKAverage(self) -> int:
        if len(self.latest_m) < self.m:
            return -1
        else:
            return (self.sum_m_k - self.sum_k) // (self.m - 2 * self.k)
