class MKAverage {
    int m;
    int k;
    List<Integer> stream; // a list to store all incoming elements
    TreeSet<int[]> smallestKContainer;  // store smallest k elements of the m
    TreeSet<int[]> middleContainer;  // store middle elements of the m
    TreeSet<int[]> largestKContainer;  // store largest k elements of the m
    int middleSum; // sum of the middle elements of the m
    int middleSize;  // size upper bound of the middleContainer

    public MKAverage(int m, int k) {
        this.m = m;
        this.k = k;
        this.stream = new ArrayList<>();
        Comparator<int[]> comparator = new Comparator<int[]>(){
            @Override
            public int compare(int[] a, int[] b) {
                if (a[0] == b[0]) {
                    return a[1] - b[1];
                }
                return a[0] - b[0]; // [value, index]
            }
        };
        this.smallestKContainer = new TreeSet<>(comparator);
        this.middleContainer = new TreeSet<>(comparator);
        this.largestKContainer = new TreeSet<>(comparator);
        this.middleSum = 0;
        this.middleSize = m - 2 * k;
    }

    // First-time with rearrange: O(logm + klogk)
    // Following add with rearrange: O(logm + logk)
    public void addElement(int num) {
        stream.add(num);
        middleSum += num;
        // step 1. insert to middleContainer firstly so that we can easily rearrange later
        middleContainer.add(new int[]{num, stream.size() - 1}); 
        int totalSize = smallestKContainer.size() + middleContainer.size() + largestKContainer.size();
        if (totalSize >= m) {
            if (totalSize > m) {
                // step 2. remove the element with least index among the m and reduce middleSum
                int rIdx = stream.size() - 1 - m;
                int rVal = stream.get(rIdx);
                int[] rEle = new int[]{rVal, rIdx};
                smallestKContainer.remove(rEle);
                largestKContainer.remove(rEle);
                if (middleContainer.remove(rEle)) {
                    middleSum -= rVal;
                }
            }
            // step 3. rearrange the three containers to make sure the smallest k and largest k are stored in the proper container
            rearrangeContainers(); 
        }
    }

    private void rearrangeContainers() {
        // action 1. move extra elements out of middleContainer
        if (middleContainer.size() > middleSize) {
            while (largestKContainer.size() < k) {
                int[] rMid = middleContainer.pollLast();
                largestKContainer.add(rMid);
                middleSum -= rMid[0];
            }
            while (smallestKContainer.size() < k) {
                int[] rMid = middleContainer.pollFirst();
                smallestKContainer.add(rMid);
                middleSum -= rMid[0];
            }
        }
        // action 2. swap unsorted element to the proper container
        if (middleContainer.last()[0] > largestKContainer.first()[0]) {
            int[] temp = middleContainer.pollLast();
            int[] largestKContainerFirst = largestKContainer.pollFirst();
            middleContainer.add(largestKContainerFirst);
            largestKContainer.add(temp);
            middleSum -= temp[0];
            middleSum += largestKContainerFirst[0];
        }
        if (middleContainer.first()[0] < smallestKContainer.last()[0]) {
            int[] temp = middleContainer.pollFirst();
            int[] smallestKContainerLast = smallestKContainer.pollLast();
            middleContainer.add(smallestKContainerLast);
            smallestKContainer.add(temp);
            middleSum -= temp[0];
            middleSum += smallestKContainerLast[0];
        }
    }

    public int calculateMKAverage() {
        if (stream.size() < m) {
            return -1;
        }
        return middleSum / middleSize;
    }
}

