public class MKAverage {
    Queue<(int val, int idx)> _q = null;
    int _i=0;
    int _m = 0;
    int _k = 0;
    int _runningSum = 0;
    SortedSet<(int val, int idx)> _left = null; //k-Smallest
    SortedSet<(int val, int idx)> _middle = null;//Elements that are not the k-largest or smallest. (m-2k) size
    SortedSet<(int val, int idx)> _right = null;//k-Largest

    public MKAverage(int m, int k) {
        _m = m;
        _k = k;
        _q = new Queue<(int val, int idx)>();
        _left = new SortedSet<(int val, int idx)>();
        _middle = new SortedSet<(int val, int idx)>();
        _right = new SortedSet<(int val, int idx)>();
    }

    public void AddElement(int num) {
        (int val, int idx) newElem = (num, _i++);
        _q.Enqueue(newElem);

		AddToSortedSets(newElem);

		if(_q.Count > _m){
            var rem = _q.Dequeue();
            RemoveFromSortedSets(rem);
        }

        RebalanceSortedSets();
    }

    void AddToSortedSets((int val, int idx) newElem){
        if(_left.Count==0 || newElem.val<= _left.Max.val)
            _left.Add(newElem);
        else if(_middle.Count==0 || newElem.val<= _middle.Max.val){
            _runningSum+=newElem.val;
            _middle.Add(newElem);
        }
        else 
            _right.Add(newElem);
    }

    void RemoveFromSortedSets((int val, int idx) rem){
        if(_left.Contains(rem)) 
			_left.Remove(rem);
        else if(_right.Contains(rem)) 
			_right.Remove(rem);
        else if(_middle.Contains(rem)){
            _runningSum-=rem.val;
            _middle.Remove(rem);
        }
    }

    void RebalanceSortedSets(){
        if(_left.Count>_k){
            _runningSum+=_left.Max.val;
            Move(_left, _middle, _left.Max);
        }
        else if (_left.Count<_k && _middle.Count>0){
            _runningSum-=_middle.Min.val;
            Move(_middle, _left, _middle.Min);
        }

        if(_middle.Count> (_m - 2*_k)){
            _runningSum-=_middle.Max.val;
            Move(_middle, _right, _middle.Max);
        }
        else if (_middle.Count< (_m - 2*_k) && _right.Count>0){
            _runningSum+=_right.Min.val;
            Move(_right, _middle, _right.Min);
        }
    }

    void Move (SortedSet<(int val, int idx)> fromSet, SortedSet<(int val, int idx)> toSet, (int val, int idx) valToMove){
        fromSet.Remove(valToMove);
        toSet.Add(valToMove);
    }

    public int CalculateMKAverage() {
        if(_left.Count + _middle.Count + _right.Count < _m) return -1;
        else{
            var avg = _runningSum*1.0/(_m-2*_k);
            return (int)Math.Floor(avg);
        }
    }
}
```
AddElement is O(log M), since the middle set can have m-2k elements.
CalculateMKAverage is constant time.

