from sortedcontainers import SortedList
from collections import deque

class MKAverage:

    def __init__(self, m: int, k: int):
        self.m = m
        self.queue = deque()
        self.k = k           # first index of the mid range
        self.k2 = m - k - 1  # last index of the mid range
        self._list = SortedList()
        self.sum = 0
        self.valid = self.m - 2 * self.k  # no of elements in a valid range

    def _remove(self, num: int) -> None:
        index = self._list.index(num)
        if index < self.k:
            # if num is being removed form first-k,
            # then smallest element from the mid will be absorbed by the first-k
            self.sum -= self._list[self.k]
        elif index > self.k2:
            # if num is being removed form last-k,
            # then largest element from the mid will be absorbed by the last-k
            self.sum -= self._list[self.k2]
        else:
            # num directly removed from the mid
            self.sum -= num
        self._list.remove(num)

    def _add(self, num: int, size: int) -> None:
        self._list.add(num)
        index = self._list.index(num)
        if index < self.k:
            # if num is added to first-k
            # the prev largest element from first-k will become part of mid
            self.sum += self._list[self.k]
        elif index >= size - self.k:
            # if num is added to last-k
            # the prev smallest element from last-k will become part of mid
            self.sum += self._list[size - self.k - 1]
        else:
            # num directly added to mid
            self.sum += num

    def addElement(self, num: int) -> None:
        self.queue.append(num)
        popped = None
        if len(self.queue) > self.m:
            popped = self.queue.popleft()
        if popped is None:
            size = len(self.queue)
            if size > 2 * self.k:
                # if the first-k and last-k have been filled, start adding to sum
                self._add(num, size)
            else:
                self._list.add(num)
        else:
            self._remove(popped)
            self._add(num, self.m)

    def calculateMKAverage(self) -> int:
        if len(self.queue) != self.m:
            return -1
        return self.sum // self.valid
```

