class MKAverage {
    List<Integer> list, sortedList;
    int M, K;
    int sum;
    public MKAverage(int m, int k) {
        list = new ArrayList<>();
        sortedList = new ArrayList<>();
        M = m;
        K = k;
        sum = 0;
    }

    public void addElement(int num) {
        if (list.size() == M) {
            Integer rem = list.get(0);
            if (rem < sortedList.get(K)) {
                sum = sum - sortedList.get(K) + sortedList.get(M-K);
            } else if (rem < sortedList.get(M - K)) {
                sum = sum - rem + sortedList.get(M-K);
            }
            sortedList.remove(rem);
            list.remove(0);
        } else if (list.size() == M-1) {
            sum = 0;
            for (int i = K; i < M - K; i++) {
                sum += sortedList.get(i);
            }
        }
        list.add(num);
        insert(num);
    }
    public int calculateMKAverage() {
        if (list.size() < M) return -1;
        int count = M - K - K;
        return sum / count;
    }

    private void insert(int n) {
        int start = 0;
        int end = sortedList.size()-1;
        int pos = -1;
        while (start <= end) {
            int mid = (end - start) / 2 + start;
            int val = sortedList.get(mid);
            if (val == n) {
                pos = mid;
                break;
            }
            if (val < n) {
                start = mid + 1;
            } else {
                end = mid - 1;
            }
        }
        if (pos == -1) {
            pos = start;
        }

        if (list.size() == M) {
            if (pos < K) {
                sum = sum + sortedList.get(K-1) - sortedList.get(M-K-1);
            } else if (pos < M - K) {
                sum = sum + n - sortedList.get(M-K-1);
            }
        }
        sortedList.add(pos, n);

    }
}
