class MKAverage {
    Queue<Integer> queue = new LinkedList<Integer>();
    TreeMap<Integer,Integer> hm = new TreeMap<>();
    int m, k;
    int ss  = 0;
    long[] sum = new long[2]; // sum[0] - sum of first k smallest elements, sum[1] - sum of first m-k smallest element
    int[] arr1 = new int[2]; // arr1[0] - key in map, arr1[1] - value in map, which matches kth smallest element in treemap 
    int[] arr2 = new int[2];
    public MKAverage(int m, int k) {
        this.m = m;
        this.k = k;
    }

    public void addElement(int num) {
        ss++;
        queue.offer(num);
        hm.put(num, hm.getOrDefault(num, 0)+1);
        if(ss < m) return;
        else if(ss == m) {
            initiate(arr1, k, sum, 0); // initializee arr1 with kth smallest element, add the sum at index 0
            initiate(arr2, m-k, sum, 1);
        } else {
            int remove = queue.poll();
            int cc = hm.get(remove);
            if(cc == 1) 
                hm.remove(remove);
            else 
                hm.put(remove, cc-1);
            int st1 = getState(arr1[0], remove, num);
            goToNextState(st1, arr1, remove, num, sum, 0);
            int st2 = getState(arr2[0], remove, num);
            goToNextState(st2, arr2, remove, num, sum, 1);
        }
    }

    public int calculateMKAverage() {
        if(ss < m) return -1;
        long val = (sum[1] - sum[0]) / (m - 2*k);
        return (int)val;
    }

    private void goToNextState(int st, int[] arr, int remove, int add, long[] sum, int si) {;
        switch(st) {
            case 1:
                sum[si] += add - remove;
                break;
            case 3:
                sum[si] += add;
                sum[si] -= arr[0];
                moveBack(arr);
                break;
            case 4:
                sum[si] -= remove;
                moveForward(arr);
                sum[si] += arr[0];
                break;
            case 5:
                if(arr[1] > hm.getOrDefault(arr[0], 0)) {
                    sum[si] -= arr[0];
                    moveForward(arr);
                    sum[si] += arr[0];
                }
                break;
            default:
                break;
        }
    }

    private void moveBack(int[] arr) {
        if(--arr[1] <= 0) {
            Integer prev = hm.floorKey(arr[0]-1);
            arr[0] = prev;
            arr[1] = hm.get(prev);
        }
    }

    private void moveForward(int[] arr) {
        if(++arr[1] > hm.getOrDefault(arr[0], 0)) {
            Integer next = hm.ceilingKey(arr[0]+1);
            arr[0] = next;
            arr[1] = 1;
        }
    }

    private int getState(int key, int remove, int add) {
        if(add < key && remove < key) return 1;
        if(add > key && remove > key) return 2;
        if(add == key && remove == key) return 2;
        if(add < key) return 3;
        if(remove < key) return 4;
        if(remove == key && add > key) return 5;
        return 6;
    }

    private void initiate(int[] arr, int co, long[] sum, int si) {
        // find (co)th smallest element in the map.
        // Map {1 -> 2, 2 -> 3, 3 -> 2} for k = 4, arr[0] = 2, arr[1] = 2;
        long ss = 0;
        long cc = 0;
        int lkey = 0;
        for(int key : hm.keySet()) {
            if(co == 0) break;
            cc = Math.min(co, hm.get(key));
            ss += cc * key;
            co -= (int)cc;
            lkey = key;
        }
        arr[0] = lkey;
        arr[1] = (int)cc;
        sum[si] = ss;
    }
}

