		return average(lastElems, k, lastElems.size() - k - 1);
	}

	int average(List<Integer> l, int from, int to) {
		long sum = 0L;
		for (int i = from; i <= to; i++) sum += (long) l.get(i);

		return Math.round(sum / (to - from + 1));
	}
