//multiset keeps elements in increasing order
#define ll long long
#define pb push_back

class MKAverage {
    public:
        int m,k;
        queue<int> window;//has the m elements or less in orignal order

        multiset<int> smaller;
        multiset<int> middle;
        multiset<int> larger;

        ll middleSum;

        MKAverage(int m, int k) {
            this->m=m;
            this->k=k;
        }

        int calculateMKAverage() {
            if(window.size() < m) return -1;

            return (middleSum/middle.size());
        }

        //called only 1 time
        void initialiseMultisets(){
            middleSum=0;
            queue<int> windowCopy = window;

            vector<int> mElementsSorted;

            while(windowCopy.size() > 0){
                mElementsSorted.pb(windowCopy.front());
                windowCopy.pop();
            }

            sort(mElementsSorted.begin(),mElementsSorted.end());
            int n = mElementsSorted.size();

            int i=0;
            for(;i<k;i++) smaller.insert(mElementsSorted[i]);

            for(;i<(n-k);i++){
                middle.insert(mElementsSorted[i]);
                middleSum += mElementsSorted[i];
            }

            for(;i<n;i++) larger.insert(mElementsSorted[i]);
        }

        void newElementAdjust(int num){
            //this num is also there at the window end
            int smallerLast = *(smaller.rbegin());
            int largerFirst = *(larger.begin());

            if(num < smallerLast){
                //insert num in smaller
                smaller.insert(num);
                //move last element of smaller to middle
                // smaller.erase(smaller.rbegin()); not working
                smaller.erase(--smaller.end());
                middle.insert(smallerLast);
                middleSum += smallerLast;
            }
            else if(num > largerFirst){
                //insert num in larger
                larger.insert(num);
                //move first element of larger to middle
                larger.erase(larger.begin());
                middle.insert(largerFirst);
                middleSum += largerFirst;
            }
            else{
                //insert in middle
                middle.insert(num);
                middleSum += num;
            }
        }

        void removeElementAdjust(){
            //window ka first element remove
            int windowFirst = window.front();
            window.pop();

            int smallerLast = *(smaller.rbegin());
            int largerFirst = *(larger.begin());

            int middleFirst = *(middle.begin());
            int middleLast = *(--middle.end());

            //now search this windowFirst in the 3 multisets. Jahan bhi mile remove it
            //if found in smaller, smaller ka size decreased by 1 so middle ka first ko move to smaller.
            if(windowFirst <= smallerLast){
                smaller.erase(smaller.lower_bound(windowFirst));

                middle.erase(middle.begin());
                middleSum -= middleFirst;

                smaller.insert(middleFirst);
            }
            else if(windowFirst >= largerFirst){
                larger.erase(larger.lower_bound(windowFirst));

                middle.erase(--middle.end());
                middleSum -= middleLast;

                larger.insert(middleLast);
            }
            else{
                middle.erase(middle.lower_bound(windowFirst));
                middleSum -= windowFirst;
            }
        }

        //m elements to hone do
        void addElement(int num) {
            //i'll first add then remove to keep the sizes maintaned
            window.push(num);
            if(window.size() < m) return;

            if(window.size() == m){
                initialiseMultisets();
            }
            else{
                //adjust the new elements in the multisets
                newElementAdjust(num);

                //remove the first element of window and adjust the multisets
                removeElementAdjust();
            }
        }

};
