class MKAverage {

    int m; int k;
    Deque<Integer> deque;
    public MKAverage(int m, int k) {

        this.m = m;
        this.k = k;
        deque = new LinkedList<>();
    }

    public void addElement(int num) {
        deque.add(num);
        if(deque.size() > m){
            deque.removeFirst();
        }
    }

    public int calculateMKAverage() {

        if(deque.size() < m) { return -1; }

        PriorityQueue<Integer> pq1 = new PriorityQueue<>((a,b) -> a-b);
        PriorityQueue<Integer> pq2 = new PriorityQueue<>((a,b) -> b-a);

        for(int el: deque){
            pq1.offer(el);
        }
        //delete k small elements
        for(int i=0; i<k; i++){ pq1.poll(); }

        while(!pq1.isEmpty()){ pq2.offer(pq1.poll()); }
        //delete k big elements
        for(int i=0; i<k; i++){ pq2.poll(); }

        int count = pq2.size();

        long sum = 0;

        while(!pq2.isEmpty()){ sum+=pq2.poll();}

        return (int) (sum/count);
    }
}
