class MKAverage:

    def __init__(self, m: int, k: int):
        self.m = m
        self.k = k
        self.arr = []


    def addElement(self, num: int) -> None:
        self.arr.append(num)


    def calculateMKAverage(self) -> int:
        if len(self.arr) < self.m:
            return -1

        cur = self.arr[-self.m:]
        cur.sort()
        cur = cur[self.k:-self.k]
        return round(sum([x for x in cur])/(len(cur)))
