    MKAverage(int m, int k) {
        this->m = m;
        this->k = k;
        sum = 0;
        ex = 0;

    }

    void addElement(int num) {
        //printf("%d:", num);
        data.push(num);
        com.insert(num);
        sum += num;
        if(com.size()==k){
            it = com.find(*com.begin());
            it1 = com.end();
            it1--;
            ex = (sum<<1);
        } else if(com.size()>k){
            if(num<(*it1)){
                // shift left
                //printf("shift left");
                //printf("-%d %d %d", *it, *it1, num);
                ex -= (*it1);
                ex += num;
                it1--;
            }
            if(num>=*it){
                // shift right
                //printf("shift right");
                //printf("-%d %d %d", *it, *it1, num);
                ex -= (*it);
                ex += num;
                it++;
            }
        }
        if(data.size()>m){
            num = data.front();
            data.pop();

            //printf("-%d %d %d", *it, *it1, num);

            sum -= num;

            auto tt = com.find(num);
            if(num<=*it1){
                ex -= num;
                it1++;
                if(it1==tt)  it1++;
                ex += (*it1);
            }
            if(num>*it || it==tt){
                ex -= num;
                it--;
                if(it==tt)  it--;
                ex += (*it);
            }
            com.erase(tt);
        }
        /*
