class Solution {
public:
    bool isValid(string s)
    {
        if(s[0]=='b' || s[0]=='c')
        {
            return false;
        }
        stack<char> st;
        for(int i=0;i<s.length();i++)
        {
            if(s[i]=='a')
            {
                st.push(s[i]);
                // st.push(s[i]);
            }
            else if(!st.empty() && s[i]=='b')
            {
                if(st.top()=='a')
                {
                    st.pop();
                    st.push(s[i]);
                }
                else
                {
                    return false;
                }
            }
            else
            {
                if(!st.empty() && st.top()=='b')
                {
                    st.pop();
                }
                else
                {
                    return false;
                }
