class Solution {
public:
    bool isValid(string s) {
        // Wipe out 'abc' in s, and then wipe out 'abc' in the new string, recursively doing this until there is
        // no more 'abc' in the string. If the string is empty, return true, otherwise return false.
        if (s.empty())
        {
            return true;
        }

        if (s.find("abc") == string::npos)
        {
            return false;
        }

        string res;
        int size = s.size();
        for (int i = 0; i < size;)
        {
            char c1 = s[i], c2 = i + 1 < size ? s[i + 1] : ' ';
            char c3 = i + 2 < size ? s[i + 2] : ' ';
            if (c1 == 'a' && c2 == 'b' && c3 == 'c')
            {
                i += 3;
            }
            else
            {
                res += s[i];
                ++i;
            }
        }

        return isValid(res);
    }
};
```

