        if(s.size() < 3) return false;

        bool ans = false;
        size_t pos = s.find("abc", 0);
        size_t i = 0, diff = 0;
        if(pos == string::npos) return ans;

        string helper;

        for(; pos != string::npos ; pos = s.find("abc", i)) {
        	//cout << "i: " << i << " pos: " << pos << endl;
