class Solution(object):
    def isValid(self, S):
        """
        :type S: str
        :rtype: bool
        """
        if not S:
            return False
        s = []
        for char in S:
            if char == "a":
                s.append(char)
            if char == "b":
                if not s:
                    return False
                if s[-1] == "a":
                    s.pop()
                    s.append(char)
            if char == "c":
                if not s:
                    return False
                if s[-1] == 'b':
