import "unicode/utf8"

type Stack struct {
	top  int
	data []rune
}

func NewStack(maxSize int) *Stack {
	data := make([]rune, maxSize)
	return &Stack{
		top:  -1,
		data: data,
	}
}

func (s *Stack) push(ch rune) {
	s.data[s.top+1] = ch
	s.top++

}

func (s *Stack) peek(depth int) rune {
	return s.data[s.top-depth]
}

func (s *Stack) pop() rune {
	defer func() {
		s.top--
	}()
	return s.data[s.top]
}

func (s *Stack) isEmpty() bool {
	return s.top == -1
}

func (s *Stack) size() int {
	return s.top + 1
}

func isValid(s string) bool {
	n := utf8.RuneCountInString(s) // len(s) can also be used in this case as the characters are known at compile time. But len(s) may give unexpected results sometimes.
    // For example : s = "\xA3", then len(s) returns 2, whereas RuneCountInString(s) returns 1

	if s[0] != 'a' || s[n-1] != 'c' {
		return false
	}

	stack := NewStack(n)
	input := []rune(s)
	for i := 0; i < n; i++ {

		// if the char is 'a' or 'b'
		if input[i] == 'a' || input[i] == 'b' {
			stack.push(input[i])
			continue
		}

		// if the char is 'c'

		if stack.size() < 2 {
			return false
		}

		if stack.peek(0) != 'b' || stack.peek(1) != 'a' {
			return false
		} 

		stack.pop()
		stack.pop()
	}

	return stack.isEmpty()
}
```

