		if input[i] == 'a' || input[i] == 'b' {
			stack.push(input[i])
			continue
		}

		// if the char is 'c'

		if stack.size() < 2 {
			return false
		}

		if stack.peek(0) != 'b' || stack.peek(1) != 'a' {
			return false
		} 

		stack.pop()
		stack.pop()
	}

	return stack.isEmpty()
}
