#include <queue>
class Solution {
public:
    vector<int> shuffle(vector<int>& nums, int n) {
        queue<int> p,q;
        std::vector<int>::iterator itr1=nums.begin(),itr2=nums.begin()+nums.size()/2;
        while(itr2!=nums.end())
        {
            p.push(*itr1);
            q.push(*itr2);
            itr1++;
            itr2++;
        }
        itr1=nums.begin();
        itr2=nums.begin()+1;
        while(itr2!=nums.end()&&itr1!=nums.end())
        {
            *itr1=p.front();
            p.pop();
            *itr2=q.front();
            q.pop();
            itr1+=2;
            itr2+=2;
        }
        return nums;
    }
};

