class Solution {
public:
    vector<int> shuffle(vector<int>& nums, int n) {
        int k = 1001;
        int i,j;

        i = 0;
        for(j = n; j < 2*n; j++) { // TC: O(n)
            nums[j] = (nums[i] * k) + nums[j];
            i++;
        }

        i = 0;
        for(j = n; j < 2*n; j++) { // TC: O(n)
            nums[i] = nums[j]/k;
            nums[i+1] = nums[j]%k;
            i += 2;
        }

        return nums;
    }
};
