class Solution {
public:
    vector<int> shuffle(vector<int>& nums, int n) {

        // declare a res array

        vector<int> res;

        // use two pointers

        int i = 0;

        int j = n;

        // fill the res array according to condition

        while(i < n)
        {
            res.push_back(nums[i]);

            res.push_back(nums[j]);

            i++;

            j++;
        }

        return res;
    }
};
