class Solution {
    public int[] shuffle(int[] nums, int n) {
       int[] nums1 = new int[n];
int[] nums2 = new int[n*2];
       int x = 0;
        for (int i = n; i <(n*2); i++){
            nums1[x] = nums[i];
            x= x +1;
        }
        int x1 = 0;
        for (int j =0 ; j < n ; j++){
            nums2[x1] = nums[j] ;
            x1 = x1 +1;
            nums2[x1]= nums1[j];
            x1 = x1 +1;

        }
        return nums2;
    }
}

