    int maxIceCream(vector<int>& costs, int coins) {
        priority_queue<int, vector<int>, greater<int>> pq;

        for(auto &it: costs) pq.push(it);

        int count = 0;
        while(!pq.empty() && coins >= pq.top()){
            count++;
            coins -= pq.top();
            pq.pop();
        }

        return count;
    }
};
