class Solution {
private:
    int countBars(int index, int coins, vector<int>& prices) {
        if(index == 0){
            if(coins >= prices[0]) return 1;
            return 0;
        }

        int pick = 0;
        int notPick = countBars(index-1, coins, prices);
        if(coins >= prices[index])pick = 1 + countBars(index-1, coins-prices[index], prices);

        return max(pick, notPick);
    }
public:
    int maxIceCream(vector<int>& costs, int coins) {
        int n = costs.size();
        return countBars(n-1, coins, costs);
    }
};
