            return 0;
        }

        if(dp[index][coins] != -1) return dp[index][coins];

        int pick = 0;
        int notPick = countBars(index-1, coins, prices, dp);
        if(coins >= prices[index])pick = 1 + countBars(index-1, coins-prices[index], prices, dp);

        return dp[index][coins] = max(pick, notPick);
    }
public:
