class Solution {
public:
    int maxIceCream(vector<int>& costs, int coins) {
        int n = costs.size();
        vector<vector<int>> dp(n, vector<int>(coins+1, 0));

        for(int i=0;i<=coins;i++){
            if(i >= costs[0]) dp[0][i] = 1;
            else dp[0][i] = 0;
        }

        for(int index = 1; index < n; index++){
            for(int coin = 0; coin <= coins; coin++){
                int pick = 0;
                int notPick = dp[index-1][coin];
                if(coin >= costs[index]) pick = 1 + dp[index-1][coin - costs[index]];

                dp[index][coin] = max(pick, notPick);
            }
        }

        return dp[n-1][coins];
    }
};
