import java.util.*

class Solution {

    fun maxIceCream(costs: IntArray, coins: Int): Int {
        val queue = PriorityQueue<Int>(Comparator.reverseOrder())
        var totalCost = 0

        costs.forEach { cost ->
            queue.add(cost)
            totalCost += cost

            if (totalCost > coins) {
                totalCost -= queue.remove()
            }
        }

        return queue.size
    }
}
