class Solution {
    public int maxIceCream(int[] A, int C) {
        int maxc = 0, r=0;
        for(int i = 0; i < A.length; i++)
            if(A[i] > maxc) maxc = A[i];
        int[] F = new int[maxc + 1];
        for(var v:A)F[v]++;
        for(int i=1;i<=maxc &&C>=i && C-i>=0;i++)
