class Solution {
public:
    int maxIceCream(vector<int>& costs, int coins) {
        sort(costs.begin(), costs.end());
        int count = 0; // it counts maximum number of ice cream bars the boy can buy with coins.
        for(int i = 0; i < costs.size(); i++){
            if(coins >= costs[i]){
                count++;
