class Solution {
public:
    int maxIceCream(vector<int>& costs, int coins) 
    {
        auto max_ind=max_element(costs.begin(),costs.end());

        vector<int>freq(*max_ind+1);
        for(int cost:costs) freq[cost]++;

        int ans=0;

        for(int cost=1;cost<freq.size();cost++)
        {
            if(coins<cost) break;

            int count=min(coins/cost,freq[i]);
            ans+=count;
            coins-=count*cost;
        }
        return ans;
    }
};
```

