        int res = 0;
        for (int i = 1; i < MAX_COST && coins >= i; ++i) {
            if (!cnt[i]) continue;
            int buy = std::min(coins / i, cnt[i]);
            coins -= buy * i;
            res += buy;
        }
        return res;
    }
