        Arrays.sort(costs);
        int sum = 0;
        int maxIceCream = 0;
        for(int i = 0 ; i < costs.length ; i ++){
            sum = sum + costs[i];
            if(sum <= coins){
                maxIceCream++;
            }else{
                return maxIceCream;
            }
        }
        return maxIceCream;
    }
}
