class Solution {
public:
int f(int index,vector<int> &costs,int coins,vector<vector<int>> &dp)
{
    if(index==0)
    {
        if(costs[index]<=coins)
        {
            return 1;
        }
        return 0;
    }
    if(dp[index][coins]!=-1) return dp[index][coins];
    int nottake = 0+f(index-1,costs,coins,dp);
    int take = INT_MIN;
    if(costs[index]<=coins)
    {
        take = 1+f(index-1,costs,coins-costs[index],dp);
    }
    return dp[index][coins] = max(take,nottake);
}
    int maxIceCream(vector<int>& costs, int coins) {
        int n = costs.size();
        vector<vector<int>> dp(n,vector<int>(coins+1,-1));
        return f(n-1,costs,coins,dp);
