class Solution {
public:
    int maxIceCream(vector<int>& costs, int coins) {
        sort(costs.begin(),costs.end());
        int sum = costs[0], count = 1;
        for(int i = 1; i < costs.size(); i++)
        {
            if(sum < coins)
            {
                sum+=costs[i];
                count++;
            }
            else { break; }
        }
        if(sum > coins) { count -=1; }
        else if(sum == costs[0] && costs.size() != 1) { count = 0; }

        return count;
    }
};

