class Solution {
public:
    int maxIceCream(vector<int>& costs, int coins) {

       // sort the costs in ascending order as we want to find maximum number of ice creams
       // which can happen if it purchases chepest ice cream first.
        sort(costs.begin(),costs.end());
        int sum = 0;
        int count = 0;

    // if total cost > coins then that means coins get empty
    // while if cost <= coins then it can still buy ice cream so count more number of ice creams
        for(int i=0;i<costs.size();i++){
            sum += costs[i];
            if(sum <= coins){
                count++;
            }

            if(sum > coins){
                break;
            }
        }
        return count;
    }
};



class Solution:
     def maxIceCream(self, costs: List[int], coins: int) -> int:
           costs.sort()
          for i in range(len(costs)):
               coins -= costs[i]
                if coins < 0:
                   return i
          return len(costs)

          ans=0
         costs.sort()
         for x,y in enumerate(costs):
               if coins<y:
                  break
              ans=ans + 1
              costs-coins+y
        return ans

