class Solution {
    public int getMinDistance(int[] nums, int target, int start) {
        int left = start, right = start, leftFirstDiff = Integer.MAX_VALUE, rightFirstDiff =  Integer.MAX_VALUE;
        boolean leftContinue= true, rightContinue = true;
        while(left >= 0 && leftContinue){
            if(nums[left] == target){
                leftContinue = false;
                leftFirstDiff = Math.abs(left - start);
            }
            left--;
        }
        while(right < nums.length && rightContinue){
            if(nums[right] == target){
                rightContinue = false;
                rightFirstDiff = Math.abs(right - start);
            }
            right++;
        }
        return Math.min(leftFirstDiff, rightFirstDiff);
    }
}
