impl Solution {
    pub fn get_min_distance(nums: Vec<i32>, target: i32, start: i32) -> i32 {
        let len_n: i32 = nums.len() as i32;

        let mut lo: i32 = start;
        let mut hi: i32 = start;
        while lo >= 0 || hi < len_n{
            if lo >= 0 {
                if nums[lo as usize] == target{
                    return start - lo;
                }

                lo -= 1;
            }

            if hi < len_n{
                if nums[hi as usize] == target{
                    return hi - start;
                }

                hi += 1;
            }
        }

        unreachable!()
    }
}
