                int  p = std::abs(i -start);
                if (p < prev)
                {
                    prev = p;
                }
            }
            i++;
        }
        if (prev == std::numeric_limits<int>::max())
        {
            // nothing found
            return 0;
        }
        return prev;
    }
};
