class Solution {
    public int getMinDistance(int[] nums, int target, int start) {
        int n = nums.length;
        int i = start;
        int j = start;
        int minDist = Integer.MAX_VALUE;
        while(i >= 0){
            if(nums[i] == target) {
                minDist = Math.min(minDist, Math.abs(i - start));
                break;
            }
            i--;
        }

        while(j < n){
            if(nums[j] == target) {
                minDist = Math.min(minDist, Math.abs(j - start));
                break;
            }
            j++;
        }
        return minDist;
    }
}
