class Solution:
    def splitString(self, s: str, sentinel = int(1e9 + 7)) -> bool:
        N = len(s)
        ok = lambda pre, cur: pre == sentinel or pre == 1 + cur  # ✅ monotonically decreasing
        @cache                                                   # 🤔 memo
        def go(i = 0, pre = sentinel, steps = -1):
            if i == N:
                return 0 < steps                                 # 🛑 base case
            for j in range(i + 1, N + 1):
                cur = int(s[i:j])
                if ok(pre, cur) and go(j, cur, 1 + steps):       # 🚀 recursive case
                    return True
            return False
        return go()
