    private boolean dfs(String s, int prev, int i, int lastNum){
        int cur=prev*10+s.charAt(i)-'0';
        if (cur==lastNum-1){
            if (i+1>=s.length()){
                return true;
            }
            if (dfs(s,0,i+1,cur))
                return true;
            if (dfs(s,cur,i+1,lastNum))
                return true;
        } else {
            if (i+1>=s.length()){
                return false;
            }
            if (dfs(s,cur,i+1,lastNum))
                return true;
            if (lastNum==-1 && dfs(s,0,i+1,cur)){
                return true;
            }
        }
        return false;
    }
}
