public bool SplitString(string s)
{
	bool result = canSplit("0", s, 0, 0);
	return result;
}

private bool canSplit(string firstPart, string s, int start, int currentLength)
{
	if (start >= s.Length && currentLength != s.Length)
	{
		return true;
	}

	for (int length = 1; length <= s.Length - start; length++)
	{
		string nextPart = s.Substring(start, length);
		if ((start == 0 || hasDiffOne(firstPart, nextPart)) && canSplit(nextPart, s, start + length, length))
		{
			return true;
		}
	}

	return false;
}

private bool hasDiffOne(string first, string second)
{
	first = first.TrimStart('0');
	second = second.TrimStart('0');

	if (string.IsNullOrEmpty(first) && string.IsNullOrEmpty(second))
	{
		return false;
	}

	if (string.IsNullOrEmpty(second))
	{
		return first == "1";
	}

	if (string.IsNullOrEmpty(first))
	{
		return false;
	}

	bool isLast = first[^1] - '0' == (second[^1]  - '0'  + 1) % 10;
	int carry = (second[^1] - '0' + 1) >= 10 ? 1 : 0;

	if (!isLast)
	{
		return false;
	}

	int i = first.Length - 2, j = second.Length - 2;

	while (i >= 0 && j >= 0)
	{
		if ((first[i] - '0') != (second[j] - '0' + carry) % 10)
		{
			return false;
		}

		carry = (second[j] - '0' + carry) >= 10 ? 1 : 0;
		i--;
		j--;
	}

	if (carry > 0)
	{
		return j == -1 && i == 0 && first[i] == '1';
	}

	return i == j;
}
