class Solution:
    def splitString(self, s: str) -> bool:

        def DFS(index, prev):
			# base case: no character left to split
            if index == n:
                return True
			# split the left substrings
            for j in range(index, n):
                val = int(s[index:j+1])
                if prev - val == 1 and DFS(j+1, val):
                    return True

		# split s to get prev
		n = len(s)
        for i in range(n-1):       # at least two substring
            val = int(s[:i+1])
            if DFS(i+1, val):
                return True
        return False 
