class Solution {
public:
    bool dfs(string &s, int idx, long prev, int dep) {
        if (idx == s.size() && dep != 1)
            return true;
        long num = 0;
        for (int i = idx; i < s.size() && log10(num) < 10; i++) {
            num = 10 * num + s[i] - '0';
            if (prev == -1 || num == prev - 1) {
                if (dfs(s, i + 1, num, dep + 1))
                    return true;
            }
        }
        return false;
    }
    bool splitString(string s) {
        return dfs(s, 0, -1, 0);
    }
};
