class Solution {
  public:
    bool f = false;
  void recurse(string & s, int start, vector < string > & v) {
    if (start == s.size()) {
      if (v.size() > 1) {
        bool pos = true;
        unsigned long long prev = stoull(v[0]);
        for (int i = 1; i < v.size(); i++) {
          unsigned long long curr = stoull(v[i]);
          if ((prev - curr) != 1) {
            pos = false;
            break;
          }
          prev = curr;
        }
        f |= pos;
      }
    }

    for (int si = 1; si <= s.size() - start; si++) {
      v.push_back(s.substr(start, si));
      recurse(s, start + si, v);
      v.pop_back();
    }
  }

  bool splitString(string s) {
    vector < string > v;
    recurse(s, 0, v);
    return f;
  }
};
```

