#define ll long long int
class Solution {
public:
    //Max value of long long int . one can get it with use of LONG_MAX
    string LMAX="9223372036854775807";
    // Converting string to long long int
    ll con(string &s){
        ll ans;
        int i=0;
        //For loop to determine index of first non-zero digit
        for(;i<(int)s.size();i++){
            if(s[i]=='0'){
                continue;
            }
            else
                break;
        }
        if(i==(int)s.size())
            return 0;
        string res=s.substr(i);
        ll sz=res.size();
        //if resultant string contains more than 19 digits: NOT POSSIBLE
        if(sz>19)
            return -1;
        bool f1=false;
        // if resultant string contains exactly 19 digits, we need to check whether it is smaller or larger than LONG_MAX
        if(sz==19){
            for(int i=0;i<sz;i++){
                if(res[i]>LMAX[i]){
                    if(f1==false){
                        return -1;
                    }
                }
                else if(res[i]<LMAX[i]){
                    f1=true;
                }
            }
            //If greater than LONG_MAX :-> NOT POSSIBLE
            if(!f1)
            return -1;
        }
        //convert string to long long int
        ans=stoll(res);
        return ans; 
    }
    //ind -> current index, s-> original string, n-> size of string , prev-> previous number
    bool find(int ind,string &s,int &n,ll prev){
        //If we reach end of the string ,it means we got the answer
        if(ind==n){
            return true;
        }
        string z="";
        bool res=false;
        for(int i=ind;i<n;i++){
            z+=s[i];
            ll num=con(z);
            //Check for descending order and difference of adjacent element should be 1, then only proceed forward
            if(prev-num==1){
                res=res or find(i+1,s,n,num);
            }
        }
        return res;
    }
    bool splitString(string s) {
        int n=s.size();
        bool res=false;
        string curr="";
        if(n==1)
        {
            return false;
        }
        if(n==2){
            ll z=s[0]-'0';
            ll y=s[1]-'0';
            if(z-y==1){
                return true;
            }
            return false;
        }
        //We need to run the loop till n-1 only
        for(int i=0;i<n-1;i++){
            curr+=s[i];
            ll num=con(curr);
            res=res or find(i+1,s,n,num);
        }
        return res;

    }
};
```

