var splitString = function(s) {
    let ret = false;

    const helper = (pos, count, pre) => {
        if (ret) return;
        if (pos == s.length && count >= 2) {
            ret = true;
            return;
        }
        for (let i = pos+1; i <= s.length; i++) {
            let n = parseInt(s.substring(pos, i));
            if (pre == n + 1 || count == 0) {
                helper(i, count+1, n);
            } else if (n > pre){
                break;
            }
        }
    }

    helper(0, 0, -1);
    return ret;
};
